@ApplicationScoped
public class PostQuantumCryptoService {

    @Inject
    NTRUEngine ntruEngine;

    @Inject
    KeyManagementService keyManager;

    public CompletionStage<EncryptedData> encryptData(
        byte[] data, 
        String recipientId) {

        return keyManager.getPublicKey(recipientId)
            .thenCompose(publicKey -> 
                ntruEngine.encrypt(data, publicKey))
            .thenApply(this::wrapEncryptedData);
    }

    public CompletionStage<byte[]> decryptData(
        EncryptedData encryptedData, 
        String privateKeyId) {

        return keyManager.getPrivateKey(privateKeyId)
            .thenCompose(privateKey -> 
                ntruEngine.decrypt(encryptedData, privateKey));
    }
}
